
define([
  "dojo/_base/declare",
  "dojo/topic",
  "dojo/when",
  "dojo/aspect",
  "hotplate/hotDojoStores/stores",
  "hotplate/hotDojoStores/AppStoreNotify",
  "hotplate/hotDojoCometMessages/messages",
  "hotplate/bd/globals",

  "dojo/domReady!",
   ], function(
   declare
 , topic
 , when
 , aspect
 , stores
 , dummy
 , messages
 , globals
 ){


  var r = {};
  var userConfigStore      = stores('userConfig',      { workspaceIdCall: globals.workspaceId } ),
      workspaceConfigStore = stores('workspaceConfig', { workspaceIdCall: globals.workspaceId } ),
      accessConfigStore    = stores('accessConfig',    { workspaceIdCall: globals.workspaceId } );

  userConfigStore.memCache.put( vars.bd.userConfig, { dontPublish: true }  );
  workspaceConfigStore.memCache.put( vars.bd.workspaceConfig, { dontPublish: true }  );
  accessConfigStore.memCache.put( vars.bd.accessConfig, { dontPublish: true }  );

  // Assign the config variables
  r.userConfig = vars.bd.userConfig;
  r.workspaceConfig = vars.bd.workspaceConfig;
  r.accessConfig = vars.bd.accessConfig;


  topic.subscribe('storeUpdate', function( from, message){
    if( message.storeName === 'userConfig' && message.objectId === globals.userId ){
      r.userConfig = message.object;
      topic.publish('configChange/user', r.accessConfig, message );
    }
  });

  topic.subscribe('storeUpdate', function( from, message ){
    if( message.storeName === 'workspaceConfig' && message.objectId === globals.workspaceId ){
      r.workspaceConfig = message.object;
      topic.publish('configChange/workspace', r.workspaceConfig, message );
    }
  });

  topic.subscribe('storeUpdate', function( from, message ){
    if( message.storeName === 'accessConfig' && message.objectId === globals.userId ){
      r.accessConfig = message.object;
      topic.publish('configChange/access', r.workspaceConfig, message );
    }
  });


  /*
  aspect.after(userConfigStore.memCache, 'put', function(item){
    if( item._id === 'self'){
      topic.publish('configChange/user', item );
    }
  }, true);

  aspect.after(workspaceConfigStore.memCache, 'put', function(item){
    if( item._id === 'self'){
      r.workspaceConfig = workspaceConfigStore.memCache.get('self');
      topic.publish('configChange/workspace', item );
    }
  }, true);

  aspect.after(accessConfigStore.memCache, 'put', function(item){
    if( item._id === 'self'){
      r.accessConfig = accessConfigStore.memCache.get('self');
      topic.publish('configChange/access', item );
    }
  }, true);
  */

  return r;

});


