/*
INPUT: 
DESCRIPTION
A widget that will show various sub-widgets :
 * StatsUses -- possible uses of a workspace. This is a global table
 * Plivo -- settings specific for Plivo (ID/token)
 * PhoneNumbers -- phone numbers available to _all_ workspaces
*/

define([
  "dojo/_base/declare"

, "dijit/layout/TabContainer"
, "dijit/layout/ContentPane"

, "hotplate/hotClientDojo/widgets/widgets"

, "hotplate/bd/GodConfig/StatsUses"
, "hotplate/bd/GodConfig/Plivo"
, "hotplate/bd/GodConfig/PhoneNumbers"

], function(
  declare

, TabContainer
, ContentPane

, widgets

, StatsUses
, Plivo
, PhoneNumbers
){

  return declare( [ widgets.DestroyableTemplatedContainer ], {

    templateString: '' +
      '<div class="god-config">\n' +

      '  <div data-dojo-type="dijit/layout/TabContainer">\n' +

      '    <div data-dojo-type="dijit/layout/ContentPane" title="Main" >\n' +

      '      <div class="box">\n'+
      '        <p class="boxTitle">Workspaces</p>\n' +
      '        <span class="config-heading">Possible workspaces uses</span>\n' +
      '        <div class="small-editable-list" data-dojo-type="hotplate/bd/GodConfig/StatsUses"></div>\n'+
      '      </div>\n' +

      '      <div class="box">\n'+
      '        <p class="boxTitle">Plivo configuration</p>\n' +
      '        <span class="config-heading">Plivo API configuration (SMS)</span>\n' +
      '        <div data-dojo-type="hotplate/bd/GodConfig/Plivo"></div>\n' +
      '      </div>\n' +

      '    </div>\n' +

      '    <div data-dojo-type="dijit/layout/ContentPane" title="Phone numbers">\n' +
      '      <span class="config-heading">Phone numbers</span>\n' +
      '      <div data-dojo-type="hotplate/bd/GodConfig/PhoneNumbers"></div>\n' +
      '    </div>\n' +

      '  </div>\n' +

      '</div>\n' +
      '',
  });

});
