/*
INPUT: 
DESCRIPTION
A widget that will manipulate the one-record table `Plivo`, which has the system-wide informaton about Plivo (ID and token)
*/
define([

  "dojo/_base/declare"

, "dijit/form/Form"
, "dijit/form/ValidationTextBox"

, "hotplate/hotClientDojo/widgets/BusyButton"
, "hotplate/hotClientDojo/widgets/EditingWidget"
, "hotplate/hotClientDojo/widgets/Dummy"
, "hotplate/hotClientDojo/storeConfig/ConfigVars"

], function(

  declare

, Form
, ValidationTextBox

, BusyButton
, EditingWidget
, Dummy
, ConfigVars
){

  return declare( [ EditingWidget ], {

    templateString: '' +
    '<div class="plivo-config">\n' +
    '  <div data-dojo-type="hotplate/hotClientDojo/widgets/Dummy"></div>\n' +
    '  <form data-dojo-type="dijit/form/Form" data-dojo-attach-point="formWidget" method="POST">\n' +
    
    '      <div class="inputField">\n' +
    '        <label for="${id}_server">Plivo Auth ID</label>\n' +
    '        <input id="${id}_account" name="account" data-dojo-type="dijit/form/ValidationTextBox" size="60" maxlength="255" data-dojo-props="required:false" />\n' +
    '      </div>\n' +

    '      <div class="inputField">\n' +
    '        <label for="${id}_token">Plivo Auth Token</label>\n' +
    '        <input id="${id}_token" name="token" data-dojo-type="dijit/form/ValidationTextBox" size="60" maxlength="255" data-dojo-props="required:false" />\n' +
    '      </div>\n' +

    '      <input class="formSubmit" type="submit" data-dojo-attach-point="buttonWidget" data-dojo-type="hotplate/hotClientDojo/widgets/BusyButton" label="Update" />' +

    '  </form>\n' +
    '</div>\n' +
    '',

    storeName: 'plivoConfig',
    storeParameters: {},
    recordId: ConfigVars.plivoConfig.globalId,
  });

});
