define([
  "dojo/_base/declare"

, "dijit/layout/BorderContainer"
, "dijit/layout/TabContainer"
, "dijit/layout/ContentPane"

, "dgrid/Grid"
, "dgrid/List"
, "dgrid/OnDemandList"
, "dgrid/Selection"
, "dgrid/Keyboard"
, "dgrid/extensions/DijitRegistry"
, "dgrid/extensions/DnD"
, "dojo/dnd/Source"

, "put-selector/put"

, "hotplate/hotDojoAuth/panels/Manager"
, "hotplate/hotDojoWidgets/EditingWidget"
, "hotplate/hotDojoDgridWidgets/EditableList"
, "hotplate/hotDojoDgridWidgets/StoreSelect"
, "hotplate/hotDojoWidgets/BusyButton"
, "hotplate/hotDojoStores/stores"
, "hotplate/hotDojoGlobals/globals"
, "hotplate/hotDojoWidgets/widgets"

], function(

  declare

, BorderContainer
, TabContainer
, ContentPane

, Grid
, List
, OnDemandList
, Selection
, Keyboard
, DijitRegistry
, DnD
, DnDSource
, put

, Manager
, EditingWidget
, EditableList
, StoreSelect
, BusyButton
, stores
, globals
, widgets

){

  return declare( [ widgets.DestroyableTemplatedContainer ], {

    templateString: '' +
      '<div class="inbox">\n' +
      '  <div style="heght:100%" data-dojo-type="dijit/layout/BorderContainer">\n' +
      '    <div style="height: 10em" data-dojo-attach-point="firstGridNode"></div>\n' +
      '    <div style="height:8em" data-dojo-attach-point="secondGridNode"></div>\n' +
      '  </div>\n'+
      '</div>\n'+
      '',


    postCreate: function(){
      var self = this;

      var data = [
        { first: "Bob", last: "Barker", age: 89 },
        { first: "Vanna", last: "White", age: 55 },
        { first: "Pat", last: "Sajak", age: 65 }
      ];

      var RegistryGrid = declare( [ Grid, DijitRegistry ] );


      self.firstGrid = new RegistryGrid({
        region: 'top',
        columns: {
            first: "First Name",
            last: "Last Name",
            age: "Age"
        }
      }, self.firstGridNode );
      self.firstGrid.renderArray( data );

      self.secondGrid = new RegistryGrid({
        region: 'center',
        columns: {
            first: "First Name",
            last: "Last Name",
            age: "Age"
        }
      }, self.secondGridNode );
      self.secondGrid.renderArray( data );



      this.inherited(arguments);

    },

  });
});



