/*
INPUT: 
DESCRIPTION
A widget that will show the list of messages for the whole workspaces (`workspacesMessages`), by including WorkspacesMessagesList WITHOUT passing contactId to it (which means that no contact-based filter will take place, hence all messages will be displayed)
*/
define([
  "dojo/_base/declare"
, "hotplate/hotClientDojo/widgets/widgets"
, "hotplate/bd/WorkspacesMessages/WorkspacesMessagesList"
], function(
  declare
, widgets
, WorkspacesMessagesList
){
  return declare( [ widgets.DestroyableTemplatedContainer ], {

    templateString: '' +
      '<div class="inbox">\n' +
      '  <div data-dojo-type="hotplate/bd/WorkspacesMessages/WorkspacesMessagesList" data-dojo-props="workspaceId: this.workspaceId"></div>\n' + 
      
      '</div>\n'+
      '',
  });
});
