
define([
  "dojo/_base/declare"

, "dijit/layout/TabContainer"
, "dijit/layout/ContentPane"

, "hotplate/hotClientDojo/widgets/widgets"

, "hotplate/bd/WorkspacesConfig/WorkspacesInfo"
, "hotplate/bd/WorkspacesConfig/WorkspacesEmailConfig"
, "hotplate/bd/WorkspacesConfig/WorkspacesSmsConfig"
, "hotplate/bd/WorkspacesConfig/WorkspacesContactsCategories"


], function(
  declare

, TabContainer
, ContentPane

, widgets

, WorkspacesInfo
, WorkspacesEmailConfig
, WorkspacesSmsConfig
, WorkspacesContactsCategories

){
  return declare( [ widgets.DestroyableTemplatedContainer ], {

    templateString: '' +
      '<div class="workspaces-config">\n' +

      '  <div data-dojo-type="dijit/layout/TabContainer">\n' +

      '    <div data-dojo-type="dijit/layout/ContentPane" title="Main" >\n' +

      '      <div class="box">\n' +
      '        <p class="boxTitle">Workspace Information</p>\n' +
      '        <div class="workspaces-info" data-dojo-type="hotplate/bd/WorkspacesConfig/WorkspacesInfo" data-dojo-props="workspaceId: this.workspaceId"></div>\n'+
      '      </div>\n' +

      '      <div class="box">\n'+
      '        <p class="boxTitle">Contacts</p>\n' +
      '        <span class="config-heading">Contacts\' categories</span>\n' +
      '        <div class="auto-grow-dgrid" data-dojo-type="hotplate/bd/WorkspacesConfig/WorkspacesContactsCategories" data-dojo-props="workspaceId: this.workspaceId"></div>\n'+
      '      </div>\n' +

      '    </div>\n' +

      '    <div data-dojo-type="dijit/layout/ContentPane" title="Email" >\n' +

      '      <div class="box">\n'+
      '        <p class="boxTitle">Email settings</p>\n' +
      '        <div class="workspaces-email-config" data-dojo-type="hotplate/bd/WorkspacesConfig/WorkspacesEmailConfig" data-dojo-props="workspaceId: this.workspaceId"></div>\n'+
      '      </div>\n' +
      '    </div>\n' +

      '    <div data-dojo-type="dijit/layout/ContentPane" title="SMS" >\n' +

      '      <div class="box">\n'+
      '        <p class="boxTitle">SMS settings</p>\n' +
      '        <div class="workspaces-sms" data-dojo-type="hotplate/bd/WorkspacesConfig/WorkspacesSmsConfig" data-dojo-props="workspaceId: this.workspaceId"></div>\n'+

      '      </div>\n' +
      '    </div>\n' +


      '  </div>\n' +

      '</div>\n' +
      '',
  });

});
