/*
INPUT:
* 
DESCRIPTION
A select widget with the list of possible uses of a workspace (`statsUses`)
*/

define( [

  "dojo/_base/declare"
, "put-selector/put"
, "hotplate/hotClientDojo/dgridWidgets/StoreSelect"

], function(
  declare
, put
, StoreSelect
){
  return declare( [ StoreSelect ], {

    ownClass: 'workspaces-stats-uses-select',

    storeName: 'statsUses',
    storeParameters: { },
    renderRow: function( object ){
      var row = put('div.row', object.name );
      return row;
    },

  });
});


