
define([

  "dojo/_base/declare"

, "dijit/form/Form"
, "dijit/form/ValidationTextBox"

, "hotplate/hotDojoStores/stores"
, "hotplate/hotDojoWidgets/BusyButton"
, "hotplate/hotDojoWidgets/EditingWidget"
, "hotplate/hotDojoGlobals/globals"

], function(

  declare

, Form
, ValidationTextBox

, stores
, BusyButton
, EditingWidget
, globals

){

  return declare( [ EditingWidget ], {

    templateString: '' +
    '<div>\n' +
    '  <form data-dojo-type="dijit/form/Form" data-dojo-attach-point="formWidget" method="POST">\n' +
    '    <div class="box">\n' +
    '      <p class="boxTitle">Workspace Information</p>\n' +
    '      <div class="inputField">\n' +
    '        <label for="${id}_longName">Workspace name</label>\n' +
    '        <input id="${id}_longName" name="longName" data-dojo-type="dijit/form/ValidationTextBox" size="10" maxlength="50" data-dojo-props="required:true" />\n' +
    '      </div>\n' +
    '      <div class="inputField">\n' +
    '        <label for="${id}_tag">Workspace tag line</label>\n' +
    '        <input id="${id}_tag" name="tag" data-dojo-type="dijit/form/ValidationTextBox" size="40" maxlength="150" data-dojo-props="required:true" />\n' +
    '      </div>\n' +
    '      <input class="formSubmit" type="submit" data-dojo-attach-point="buttonWidget" data-dojo-type="hotplate/hotDojoWidgets/BusyButton" label="Update" />' +
    '    </div>\n' +
    '  </form>\n' +
    '</div>\n' +
    '',

    store: stores('WorkspacesInfo', {} ),
    storeName: 'WorkspacesInfo',
    recordId: globals.workspaceId,

  });

});
