
define( [

  "dojo/_base/declare"

, "put-selector/put"

, "hotplate/hotClientDojo/dgridWidgets/StoreSelect"
, "hotplate/hotClientDojo/stores/stores"

], function(

  declare

, put

, StoreSelect
, stores

){

  return declare( [ StoreSelect ], {

    postMixInProperties: function(){
      this.inherited(arguments);
      this.initialFilter = { workspaceIdFilter: this.workspaceId };
    },

    ownClass: 'workspaces-phone-number-select',

    storeName: 'phoneNumbers',
    storeParameters: { },
    //storeParameters: { workspaceId: globals.workspaceId },
    renderRow: function( object ){
      var row = put('div.row', object.fullNumber + ' ' + object.provider + ' ' + object.type );
      return row;
    },

  });
});


