/*
INPUT:
* workspaceId 
DESCRIPTION
A selet list of phone numbers available to the workspace (`phoneNumbers`). Setting `workspaceIdFilter` will mean that the store will respond with a list of phone number that will include (a) all phone numbers belonging to `workspaceId` (b) all phone numbers marked as 'shared'.
*/
define( [
  "dojo/_base/declare"
, "put-selector/put"
, "hotplate/hotClientDojo/dgridWidgets/StoreSelect"
], function(
  declare
, put
, StoreSelect
){

  return declare( [ StoreSelect ], {
    postMixInProperties: function(){
      this.inherited(arguments);

      this.initialFilter = { workspaceIdFilter: this.workspaceId };
    },
    ownClass: 'workspaces-phone-number-select',
    storeName: 'phoneNumbers',
    storeParameters: { },
    renderRow: function( object ){
      var row = put('div.row', object.fullNumber + ' ' + object.provider + ' ' + object.type );
      return row;
    },
  });
});


