
define( [

  "dojo/_base/declare"

, "put-selector/put"

, "hotplate/hotClientDojo/dgridWidgets/StoreSelect"
, "hotplate/hotClientDojo/stores/stores"
, "hotplate/hotClientDojo/globals/globals"

], function(

  declare

, put

, StoreSelect
, stores
, globals

){

  return declare( [ StoreSelect ], {

    postMixInProperties: function(){
      this.inherited(arguments);
      this.storeParameters = { workspaceId: this.workspaceId };
    },

    ownClass: 'workspaces-contacts-categories-select',

    storeName: 'workspacesContactsCategories',

    // query: { name: "O" },
    queryOptions: { placeNew: 'last' },
    renderRow: function( object ){
      var row = put('div.row', object.name );
      return row;
    },

  });
});


