/*
INPUT: 
  * workspaceId
  * contactId
DESCRIPTION
A select menu that will show the email addresses associated to a contact (`workspacesContactsEmails`)
TODO: duplicate
*/
define( [
  "dojo/_base/declare"
, "put-selector/put"
, "hotplate/hotClientDojo/dgridWidgets/StoreSelect"
], function(
  declare
, put
, StoreSelect
){
  return declare( [ StoreSelect ], {
    ownClass: 'workspaces-messages-emails-select',
    storeName: 'workspacesContactsEmails',
    renderRow: function( object ){
      var row = put('div.row', object.email );
      return row;
    },
    postMixInProperties: function(){
      this.inherited(arguments);
      this.storeParameters = { workspaceId: this.workspaceId, contactId: this.contactId };  
    },
  });
});