define( [

  "dojo/_base/declare"

, "put-selector/put"

, "hotplate/hotDojoDgridWidgets/StoreSelect"
, "hotplate/hotDojoStores/stores"
, "hotplate/hotDojoGlobals/globals"

], function(

  declare

, put

, StoreSelect
, stores
, globals

){

  return declare( [ StoreSelect ], {

    ownClass: 'workspaces-messages-phone-numbers-select',

    storeName: 'workspacesContactsPhoneNumbers',
    storeParameters: { workspaceId: globals.workspaceId },
    renderRow: function( object ){
      var row = put('div.row', object.fullNumber );
      return row;
    },
    
  });
});