/*
INPUT: 
  * workspaceId
  * contactId
DESCRIPTION
A select box with the phone numbers associated to a specific contact. 
*/

define( [

  "dojo/_base/declare"

, "put-selector/put"

, "hotplate/hotClientDojo/dgridWidgets/StoreSelect"

], function(

  declare

, put

, StoreSelect

){

  return declare( [ StoreSelect ], {

    ownClass: 'workspaces-messages-phone-numbers-select',

    storeName: 'workspacesContactsPhoneNumbers',
    renderRow: function( object ){
      var row = put('div.row', object.fullNumber );
      return row;
    },
    
    postMixInProperties: function(){
      this.inherited(arguments);
      
      this.storeParameters = { workspaceId: this.workspaceId, contactId: this.contactId };  
    },

  });
});