define([
  "dojo/_base/declare"

, "hotplate/hotDojoDgridWidgets/commonMixins"
, "hotplate/hotDojoWidgets/ViewingWidget"
, "hotplate/hotDojoWidgets/widgets"
, "hotplate/hotDojoStores/stores"
, "hotplate/hotDojoGlobals/globals"

], function(
  declare

, commonMixins
, ViewingWidget
, widgets
, stores
, globals

){

  var templateString = '' +
    '<div class="workspaces-messages-view">\n' +
    '  <div data-dojo-attach-point="messageNode"></div>\n' +
    '</div>\n'+
    '';

  return declare( [ ViewingWidget ], {

  	templateString: templateString,

    postCreate: function(){
      this.inherited(arguments);
    },

    renderInfo: function(){
    	var self = this;
    	debugger;
    	this.messageNode.innerHTML = this.info.onlineMessages.data.bodyHtmlSane;

    }

  });

});



