define([

  "dojo/_base/declare"

, "dojo/on"
, "dojo/dom"
, "dijit/form/Form"
, "dijit/form/ValidationTextBox"
, "dijit/form/DateTextBox"
, "hotplate/hotDojoWidgets/UTCDateTextBox"
, "dijit/form/TimeTextBox"

, "hotplate/hotDojoStores/stores"
, "hotplate/hotDojoWidgets/BusyButton"
, "hotplate/hotDojoWidgets/EditingWidget"
, "hotplate/hotDojoGlobals/globals"

], function(

  declare

, on
, dom
, Form
, ValidationTextBox
, DateTextBox
, UTCDateTextBox
, TimeTextBox

, stores
, BusyButton
, EditingWidget
, globals

){

  var logger = function( submitLabel, line ){
    var tabId = vars.tabId || 'Non definito';
    stores( 'workspacesNinjaFormLogs', { workspaceId: globals.workspaceId } ).put( { line: line, subsystem: 'client', tabId: tabId, submitLabel: submitLabel } );
  };
  return logger;
  
});

