define([
  "dojo/_base/declare"

, "dijit/Dialog"

, "hotplate/hotClientDojo/widgets/ViewingWidget"

, "hotplate/bd/WorkspacesMessages/WorkspacesSelectContactFrom"
, "hotplate/bd/UsersConfig"


], function(
  declare

, Dialog

, ViewingWidget

, WorkspacesSelectContactFrom
, UsersConfig

){

  return declare( [ ViewingWidget ], {

  	templateString: '' +
      '<span>\n' +
      '  Your main address: <span data-dojo-attach-point="mainEmailAddressNode"></span>\n' +
      '</span>\n'+
      '',

    renderInfo: function(){
    	var self = this;

      var record = self.info.workspacesContacts.data;
      this.mainEmailAddressNode.innerHTML = record._children.mainEmailId ? record._children.mainEmailId.email : 'Not set';
    },
   

  });

});



