
define( [

  "dojo/_base/declare"

, "put-selector/put"

, "hotplate/hotDojoDgridWidgets/StoreSelect"
, "hotplate/hotDojoStores/stores"
, "hotplate/hotDojoGlobals/globals"

], function(

  declare

, put

, StoreSelect
, stores
, globals

){

  return declare( [ StoreSelect ], {

    ownClass: 'workspaces-contacts-emails-select',

    storeName: 'workspacesContactsEmails',
    storeParameters: { workspaceId: globals.workspaceId },
    //queryOptions: { placeNew: 'last' },
    //initialFilter: { enabled: true },
    renderRow: function( object ){
      var row = put('div.row', object.email );
      return row;
    },

  });
});


