define( [], function(){


  // DEFINE SOME GLOBAL VARIABLES USED A LOT IN THE APPLICATION
  // 
  // * `r.userId`:      The currently logged in userId. Might come from
  //                    hotAppcontainer or from hotDojoAuth.
  //                    It comes from a global variable
  //
  // * `r.workspaceId`: The workspaceId the user is currently logged into
  //
  // * `e.inContainer`: Only `true` if the rendered page is the app container
  // 

  r = {}

  r.userId = null;
  r.workspaceId = null;
  r.inContainer = false;
  if( typeof( vars.hotDojoAppContainer ) !== 'undefined' ){
    r.userId = vars.hotDojoAppContainer.userId;
    r.workspaceId = vars.hotDojoAppContainer.workspaceId;
    r.inContainer = true;
  } else if( typeof( vars.hotDojoAuth ) !== 'undefined' ){
    r.userId = vars.hotDojoAuth.userId;
  }
  
  return r;
 
});
